mapper.select:
select p.* from( select rownum,rownum as rn,a.* from ( select ID as id, ROLE_NAME as roleName, ROLE_NO as roleNo, DESCRIPTION as description, OPEN_FLAG as openFlag, ROLE_NAME_EN as roleNameEn, get_dictValue_by_dictKey('QYZTDM',OPEN_FLAG) as openFlagZw from SYS_ROLE where 1=1
and OPEN_FLAG = #{openFlag,jdbcType=VARCHAR}
and ROLE_NAME like '%' || #{roleName,jdbcType=VARCHAR} || '%' escape '\'
and DESCRIPTION like '%' || #{description,jdbcType=VARCHAR} || '%' escape '\'
order by ROLE_NO ASC, CREATE_DATE desc ) a
where rownum <= #{end,jdbcType=INTEGER}
) p
where rn >= #{begin,jdbcType=INTEGER}

mapper.delete:
        delete from SYS_ROLE_PERMIS
