	         AND PERMISSION_ID IN

			 #{item,jdbcType=VARCHAR}

mapper.select:
select ID as id, ROLE_ID as roleId, PERMISSION_ID as permissionId from SYS_ROLE_PERMIS where 1=1
and ROLE_ID = #{roleId}
and PERMISSION_ID = #{permissionId}
