mapper.update:
UPDATE t_task
TASK_NO=#{taskNo,jdbcType=VARCHAR}, TASK_NAME=#{taskName,jdbcType=VARCHAR}, TASK_CONTENT=#{taskContent,jdbcType=VARCHAR}, GROUPID=#{groupid,jdbcType=VARCHAR}, PGROUPID=#{pgroupid,jdbcType=VARCHAR}, BCRWID=#{bcrwid,jdbcType=VARCHAR}, YJRWID=#{yjrwid,jdbcType=VARCHAR}, FQR=#{fqr,jdbcType=VARCHAR}, JSR_LXFS=#{jsrLxfs,jdbcType=VARCHAR}, JSR=#{jsr,jdbcType=VARCHAR}, FQ_TIME=#{fqTime,jdbcType=TIMESTAMP}, FKJZ_TIME=#{fkjzTime,jdbcType=TIMESTAMP}, LXFS=#{lxfs,jdbcType=VARCHAR}, FKZT=#{fkzt,jdbcType=VARCHAR}, FK_TIME=#{fkTime,jdbcType=TIMESTAMP}, CBZT=#{cbzt,jdbcType=VARCHAR}, QSZT=#{qszt,jdbcType=VARCHAR}, QS_TIME=#{qsTime,jdbcType=TIMESTAMP}, YJZT=#{yjzt,jdbcType=VARCHAR}, YJ_TIME=#{yjTime,jdbcType=TIMESTAMP}, QRZT=#{qrzt,jdbcType=VARCHAR}, QR_TIME=#{qrTime,jdbcType=TIMESTAMP}, CREATOR=#{creator,jdbcType=VARCHAR}, CREATETIME=#{createtime,jdbcType=TIMESTAMP}, DEPARMENTCODE=#{deparmentcode,jdbcType=VARCHAR}, DEPARMENTNAME=#{deparmentname,jdbcType=VARCHAR}, LASTUPDATETIME=#{lastupdatetime,jdbcType=TIMESTAMP}, DELETEFLAG=#{deleteflag,jdbcType=VARCHAR}
WHERE ID = #{id,jdbcType=VARCHAR}

mapper.delete:
DELETE FROM t_task
WHERE id in
#{id}

mapper.select:
SELECT

FROM t_task
WHERE 1 = 1

    and ID=#{id,jdbcType=VARCHAR}


    and TASK_NO=#{taskNo,jdbcType=VARCHAR}


    and TASK_NAME=#{taskName,jdbcType=VARCHAR}


    and TASK_CONTENT=#{taskContent,jdbcType=VARCHAR}


    and GROUPID=#{groupid,jdbcType=VARCHAR}


    and PGROUPID=#{pgroupid,jdbcType=VARCHAR}


    and BCRWID=#{bcrwid,jdbcType=VARCHAR}


    and YJRWID=#{yjrwid,jdbcType=VARCHAR}


    and FQR=#{fqr,jdbcType=VARCHAR}


    and JSR_LXFS=#{jsrLxfs,jdbcType=VARCHAR}


    and JSR=#{jsr,jdbcType=VARCHAR}


    and FQ_TIME=#{fqTime,jdbcType=TIMESTAMP}


    and FKJZ_TIME=#{fkjzTime,jdbcType=TIMESTAMP}


    and LXFS=#{lxfs,jdbcType=VARCHAR}


    and FKZT=#{fkzt,jdbcType=VARCHAR}


    and FK_TIME=#{fkTime,jdbcType=TIMESTAMP}


    and CBZT=#{cbzt,jdbcType=VARCHAR}


    and QSZT=#{qszt,jdbcType=VARCHAR}


    and QS_TIME=#{qsTime,jdbcType=TIMESTAMP}


    and YJZT=#{yjzt,jdbcType=VARCHAR}


    and YJ_TIME=#{yjTime,jdbcType=TIMESTAMP}


    and QRZT=#{qrzt,jdbcType=VARCHAR}


    and QR_TIME=#{qrTime,jdbcType=TIMESTAMP}


    and CREATOR=#{creator,jdbcType=VARCHAR}


    and CREATETIME=#{createtime,jdbcType=TIMESTAMP}


    and DEPARMENTCODE=#{deparmentcode,jdbcType=VARCHAR}


    and DEPARMENTNAME=#{deparmentname,jdbcType=VARCHAR}


    and LASTUPDATETIME=#{lastupdatetime,jdbcType=TIMESTAMP}


    and DELETEFLAG=#{deleteflag,jdbcType=VARCHAR}

and rownum = 1

SELECT

FROM t_task
WHERE 1 = 1

    and ID=#{id}


    and TASK_NO=#{taskNo}


    and TASK_NAME=#{taskName}


    and TASK_CONTENT=#{taskContent}


    and GROUPID=#{groupid}


    and PGROUPID=#{pgroupid}


    and BCRWID=#{bcrwid}


    and YJRWID=#{yjrwid}


    and FQR=#{fqr}


    and JSR_LXFS=#{jsrLxfs}


    and JSR=#{jsr}


    and FQ_TIME=#{fqTime}


    and FKJZ_TIME=#{fkjzTime}


    and LXFS=#{lxfs}


    and FKZT=#{fkzt}


    and FK_TIME=#{fkTime}


    and CBZT=#{cbzt}


    and QSZT=#{qszt}


    and QS_TIME=#{qsTime}


    and YJZT=#{yjzt}


    and YJ_TIME=#{yjTime}


    and QRZT=#{qrzt}


    and QR_TIME=#{qrTime}


    and CREATOR=#{creator}


    and CREATETIME=#{createtime}


    and DEPARMENTCODE=#{deparmentcode}


    and DEPARMENTNAME=#{deparmentname}


    and LASTUPDATETIME=#{lastupdatetime}


    and DELETEFLAG=#{deleteflag}

select p.* from(
select
rownum,rownum as rn,a.* from (
select t.* from (

SELECT

    DISTINCT



        ${f}





FROM t_task


        ,${tb}



${orderByClause}

) t ) a

    where rownum <= ${end}

) p

    where rn >= ${begin}

select p.* from(
    select rownum as rn,q.* from (
        select t.* from T_TASK  t where t.deleteFlag='0'




                        and exists (select 1 from T_USERGROUP ug where ug.groupId=t.groupId and ug.userId=#{userId,jdbcType=VARCHAR})


                        and exists (select 1 from T_GROUP g where g.id=t.groupId and g.creator=#{userId,jdbcType=VARCHAR})


                        and (
                        exists (select 1 from T_USERGROUP ug where ug.groupId=t.groupId and ug.userId=#{userId,jdbcType=VARCHAR})
                        or
                        exists (select 1 from T_GROUP g where g.id=t.groupId and g.creator=#{userId,jdbcType=VARCHAR})
                        )



                    and t.taskName like '%'||#{taskName,jdbcType=VARCHAR}||'%' escape '\'


                    and t.groupid=#{groupid,jdbcType=VARCHAR}


                    and t.creator=#{creator,jdbcType=VARCHAR}


                    and t.jsr=#{jsr,jdbcType=VARCHAR}


                    and t.createtime between #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}


                    and t.fkzt=#{fkzt,jdbcType=VARCHAR}


                    and t.yjzt=#{yjzt,jdbcType=VARCHAR}


                    and t.fkzt=0 and to_char(t.fkjz_time,'yyyyMMdd')>to_char(sysdate,'yyyyMMdd')



                and 1=2


        order by t.createTime desc
    ) q

        where rownum <= #{end,jdbcType=INTEGER}

) p

    where rn >= #{begin,jdbcType=INTEGER}

select count(1) from T_TASK t
where deleteFlag='0'




                and exists (select 1 from T_USERGROUP ug where ug.groupId=t.groupId and ug.userId=#{userId,jdbcType=VARCHAR})


                and exists (select 1 from T_GROUP g where g.id=t.groupId and g.creator=#{userId,jdbcType=VARCHAR})


                and (
                    exists (select 1 from T_USERGROUP ug where ug.groupId=t.groupId and ug.userId=#{userId,jdbcType=VARCHAR})
                    or
                    exists (select 1 from T_GROUP g where g.id=t.groupId and g.creator=#{userId,jdbcType=VARCHAR})
                )



            and t.taskName like '%'||#{taskName,jdbcType=VARCHAR}||'%' escape '\'


            and t.groupid=#{groupid,jdbcType=VARCHAR}


            and t.creator=#{creator,jdbcType=VARCHAR}


            and t.jsr=#{jsr,jdbcType=VARCHAR}


            and t.createtime between #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}


            and t.fkzt=#{fkzt,jdbcType=VARCHAR}


            and t.yjzt=#{yjzt,jdbcType=VARCHAR}


            and t.fkzt=0 and to_char(t.fkjz_time,'yyyyMMdd')>to_char(sysdate,'yyyyMMdd')



        and 1=2
