mapper.update:
UPDATE t_group_backup
GROUPID=#{groupid,jdbcType=VARCHAR}, BACKUP_TIME=#{backupTime,jdbcType=TIMESTAMP}, BACKUP_REASON=#{backupReason,jdbcType=VARCHAR}, CREATOR=#{creator,jdbcType=VARCHAR}, CREATETIME=#{createtime,jdbcType=TIMESTAMP}, DEPARMENTCODE=#{deparmentcode,jdbcType=VARCHAR}, LASTUPDATETIME=#{lastupdatetime,jdbcType=TIMESTAMP}, DELETEFLAG=#{deleteflag,jdbcType=VARCHAR}, POLICE_ID=#{policeId,jdbcType=VARCHAR}
WHERE ID = #{id,jdbcType=VARCHAR}

mapper.delete:
DELETE FROM t_group_backup
WHERE id in
${id}

mapper.select:
SELECT

FROM t_group_backup
WHERE 1 = 1

	and ID=#{id,jdbcType=VARCHAR}


	and GROUPID=#{groupid,jdbcType=VARCHAR}


	and BACKUP_TIME=#{backupTime,jdbcType=TIMESTAMP}


	and BACKUP_REASON=#{backupReason,jdbcType=VARCHAR}


	and CREATOR=#{creator,jdbcType=VARCHAR}


	and CREATETIME=#{createtime,jdbcType=TIMESTAMP}


	and DEPARMENTCODE=#{deparmentcode,jdbcType=VARCHAR}


	and LASTUPDATETIME=#{lastupdatetime,jdbcType=TIMESTAMP}


	and DELETEFLAG=#{deleteflag,jdbcType=VARCHAR}


	and POLICE_ID=#{policeId,jdbcType=VARCHAR}


	ORDER BY #{sortName,jdbcType=VARCHAR}


	#{sortOrder,jdbcType=VARCHAR}

and rownum = 1

SELECT

FROM t_group_backup
WHERE 1 = 1

	and ID=#{id,jdbcType=VARCHAR}


	and GROUPID=#{groupid,jdbcType=VARCHAR}


	and BACKUP_TIME=#{backupTime,jdbcType=TIMESTAMP}


	and BACKUP_REASON=#{backupReason,jdbcType=VARCHAR}


	and CREATOR=#{creator,jdbcType=VARCHAR}


	and CREATETIME=#{createtime,jdbcType=TIMESTAMP}


	and DEPARMENTCODE=#{deparmentcode,jdbcType=VARCHAR}


	and LASTUPDATETIME=#{lastupdatetime,jdbcType=TIMESTAMP}


	and DELETEFLAG=#{deleteflag,jdbcType=VARCHAR}


	and POLICE_ID=#{policeId,jdbcType=VARCHAR}

select p.* from(
select
rownum,rownum as rn,a.* from (
select t.* from (

SELECT

	DISTINCT



		${f}





FROM t_group_backup


		,${tb}



${orderByClause}

) t ) a

	where rownum <= #{end,jdbcType=INTEGER}

) p

	where rn >= #{begin,jdbcType=INTEGER}

SELECT
,
(SELECT g.GROUPNAME FROM T_GROUP g WHERE g.ID=t.GROUPID) AS GROUPNAME,
(SELECT s.ORG_NAME FROM SYS_USER_INFO s WHERE s.USER_ID=t.CREATOR) AS CREATORNAME,
(SELECT o.ORG_NAME FROM SYS_ORGANIZATION o WHERE o.ORG_CODE=DEPARMENTCODE) as DEPARMENTNAME
FROM t_group_backup t
WHERE 1 = 1

	and ID=#{id,jdbcType=VARCHAR}


	and GROUPID=#{groupid,jdbcType=VARCHAR}


	and BACKUP_TIME=#{backupTime,jdbcType=TIMESTAMP}


	and BACKUP_REASON=#{backupReason,jdbcType=VARCHAR}


	and CREATOR=#{creator,jdbcType=VARCHAR}


	and CREATETIME=#{createtime,jdbcType=TIMESTAMP}


	and DEPARMENTCODE=#{deparmentcode,jdbcType=VARCHAR}


	and LASTUPDATETIME=#{lastupdatetime,jdbcType=TIMESTAMP}


	and DELETEFLAG=#{deleteflag,jdbcType=VARCHAR}


	and POLICE_ID=#{policeId,jdbcType=VARCHAR}


	ORDER BY #{sortName,jdbcType=VARCHAR}


	#{sortOrder,jdbcType=VARCHAR}

and rownum = 1
