mapper.update:
UPDATE t_group
PGROUPID=#{pgroupid,jdbcType=VARCHAR}, GROUPNUM=#{groupnum,jdbcType=VARCHAR}, GROUPNAME=#{groupname,jdbcType=VARCHAR}, GROUPTYPE=#{grouptype,jdbcType=VARCHAR}, BACKUP_STATU=#{backupStatu,jdbcType=VARCHAR}, BACKUP_TIME=#{backupTime,jdbcType=TIMESTAMP}, BACKUP_REASON=#{backupReason,jdbcType=VARCHAR}, CREATOR=#{creator,jdbcType=VARCHAR}, CREATENAME=#{createname,jdbcType=VARCHAR}, CREATETIME=#{createtime,jdbcType=TIMESTAMP}, DEPARMENTCODE=#{deparmentcode,jdbcType=VARCHAR}, LASTUPDATETIME=#{lastupdatetime,jdbcType=TIMESTAMP}, DELETEFLAG=#{deleteflag,jdbcType=VARCHAR}
WHERE ID = #{id,jdbcType=VARCHAR}

mapper.delete:
DELETE FROM t_group
WHERE id in
${id}

mapper.select:
SELECT

FROM t_group
WHERE 1 = 1

    and ID=#{id,jdbcType=VARCHAR}


    and PGROUPID=#{pgroupid,jdbcType=VARCHAR}


    and GROUPNUM=#{groupnum,jdbcType=VARCHAR}


    and GROUPNAME=#{groupname,jdbcType=VARCHAR}


    and GROUPTYPE=#{grouptype,jdbcType=VARCHAR}


    and BACKUP_STATU=#{backupStatu,jdbcType=VARCHAR}


    and BACKUP_TIME=#{backupTime,jdbcType=TIMESTAMP}


    and BACKUP_REASON=#{backupReason,jdbcType=VARCHAR}


    and CREATOR=#{creator,jdbcType=VARCHAR}


    and CREATENAME=#{createname,jdbcType=VARCHAR}


    and CREATETIME=#{createtime,jdbcType=TIMESTAMP}


    and DEPARMENTCODE=#{deparmentcode,jdbcType=VARCHAR}


    and LASTUPDATETIME=#{lastupdatetime,jdbcType=TIMESTAMP}


    and DELETEFLAG=#{deleteflag,jdbcType=VARCHAR}

and rownum = 1

SELECT

FROM t_group
WHERE 1 = 1

    and ID=#{id,jdbcType=VARCHAR}


    and PGROUPID=#{pgroupid,jdbcType=VARCHAR}


    and GROUPNUM=#{groupnum,jdbcType=VARCHAR}


    and GROUPNAME=#{groupname,jdbcType=VARCHAR}


    and GROUPTYPE=#{grouptype,jdbcType=VARCHAR}


    and BACKUP_STATU=#{backupStatu,jdbcType=VARCHAR}


    and BACKUP_TIME=#{backupTime,jdbcType=TIMESTAMP}


    and BACKUP_REASON=#{backupReason,jdbcType=VARCHAR}


    and CREATOR=#{creator,jdbcType=VARCHAR}


    and CREATENAME=#{createname,jdbcType=VARCHAR}


    and CREATETIME=#{createtime,jdbcType=TIMESTAMP}


    and DEPARMENTCODE=#{deparmentcode,jdbcType=VARCHAR}


    and LASTUPDATETIME=#{lastupdatetime,jdbcType=TIMESTAMP}


    and DELETEFLAG=#{deleteflag,jdbcType=VARCHAR}

select p.* from(
select
rownum,rownum as rn,a.* from (
select t.* from (

SELECT

    DISTINCT



        ${f}





FROM t_group


        ,${tb}



${orderByClause}

) t ) a

    where rownum <= #{end,jdbcType=INTEGER}

) p

    where rn >= #{begin,jdbcType=INTEGER}

 SELECT substr(GROUPNUM,19) as GROUPNUM FROM (
    SELECT GROUPNUM FROM t_group t
    WHERE GROUPNUM is not NULL AND SUBSTR (GROUPNUM, 15,4)=TO_CHAR(SYSDATE,'YYYY')
    AND  SUBSTR (GROUPNUM, 3,12)=#{deparmentcode,jdbcType=VARCHAR}
    ORDER BY SUBSTR (GROUPNUM, 19) DESC
)  	where ROWNUM=1

select p.*,
    (select count(1) from T_AJGROUP t1 where t1.groupid=p.id and t1.deleteFlag=0) as caseNum,
    (select count(1) from T_USERGROUP t2 where t2.groupid=p.id and t2.deleteFlag=0) as crewNum
from(
    select rownum as rn,q.* from (
        select g.* from T_GROUP g where g.deleteFlag=0 and g.pgroupid is null


                and (
                  exists (select 1 from t_usergroup ug where ug.groupid=g.id and ug.deleteFlag=0 and ug.userid=#{userId,jdbcType=VARCHAR})
                  or g.creator=#{userId,jdbcType=VARCHAR}
                )

                    and a.GROUPNAME like '%'||#{groupname,jdbcType=VARCHAR}||'%' escape '\'


                    and a.groupnum like '%'||#{groupnum,jdbcType=VARCHAR}||'%' escape '\'


                    and a.CREATOR=#{creator,jdbcType=VARCHAR}


                    and a.CREATETIME between #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}


                    and exists (select 1 from t_usergroup ug where ug.groupid=g.id and ug.deleteFlag=0 and ug.userid=#{crewname,jdbcType=VARCHAR})


                    and a.deparmentcode=#{deparmentcode,jdbcType=VARCHAR}


                    exists (select 1 from t_ajgroup aj where aj.groupid=g.id and aj.deleteFlag=0 and aj.ajbh like '%'||#{userId,jdbcType=VARCHAR}||'%' escape '\')


                    and a.BACKUP_STATU=#{backupStatu,jdbcType=VARCHAR}



              and 1=2


        order by g.createTime desc
    ) q

        where rownum <= #{end,jdbcType=INTEGER}

) p

    where rn >= #{begin,jdbcType=INTEGER}

select count(1) from T_GROUP g where g.deleteFlag=0 and g.pgroupid is null


        and (
        exists (select 1 from t_usergroup ug where ug.groupid=g.id and ug.deleteFlag=0 and ug.userid=#{userId,jdbcType=VARCHAR})
        or g.creator=#{userId,jdbcType=VARCHAR}
        )

            and a.GROUPNAME like '%'||#{groupname,jdbcType=VARCHAR}||'%' escape '\'


            and a.groupnum like '%'||#{groupnum,jdbcType=VARCHAR}||'%' escape '\'


            and a.CREATOR=#{creator,jdbcType=VARCHAR}


            and a.CREATETIME between #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}


            and exists (select 1 from t_usergroup ug where ug.groupid=g.id and ug.deleteFlag=0 and ug.userid=#{crewname,jdbcType=VARCHAR})


            and a.deparmentcode=#{deparmentcode,jdbcType=VARCHAR}


            exists (select 1 from t_ajgroup aj where aj.groupid=g.id and aj.deleteFlag=0 and aj.ajbh like '%'||#{userId,jdbcType=VARCHAR}||'%' escape '\')


            and a.BACKUP_STATU=#{backupStatu,jdbcType=VARCHAR}



        and 1=2

SELECT a.name,a.num FROM (
SELECT max(t1.groupname) as name,count(1) as num,max(t1.createtime) as createtime FROM t_group t1,T_AJGROUP t2 WHERE t1.id=t2.groupid
AND t1.DELETEFLAG=0

    AND t1.BACKUP_STATU=#{backupStatus,jdbcType=VARCHAR}

GROUP BY t1.id
) a ORDER BY a.createtime
